import React, { useState, useEffect } from "react";
import DetalleBusquedaCategorias from "./DetalleBusquedaCategorias";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2'
import EncabezadoCategorias from "./EncabezadoCategorias";

const BusquedaCategorias = (props) => {  
    const [Gestor, setGestor] = useState({ Nombre_Categoria: '' });
    const [ListaCategorias, setListaCategorias] = useState([]);
    const navigate = useNavigate();

    const teclaPresionada = (e) => {
        const { name, value } = e.target;
        setGestor({ ...Gestor, [name]: value });
    }

    const ClickBtnNuevo = () => {
        props.CambiarIdCategoria(0);
        navigate("/TipoProducto/DetalleCategorias");
    }

    const DetallesCategorias = (Descripcion) => {
        props.CambiarIdCategoria(Descripcion);
        navigate("/TipoProducto/DetalleCategorias");
    }

    const ClickBtnDelete = async (ID_TipoProducto) => {
        let URL = `http://localhost:${props.ApiPort}/api/ApiTipoProducto/EliminarTipoProducto?ID_TipoProducto=${ID_TipoProducto}`;
        let Result = await fetch(URL, { method: 'DELETE' }).then(response => response.json()).catch(error => console.log(error));
        if (Result.Codigo >= 0) {
            Swal.fire({ icon: "success", title: "Categoría eliminada exitosamente", showConfirmButton: false, timer: 1500 });
            ClickBtnBuscar();
        } else {
            Swal.fire({ icon: "error", title: "Oops...", text: Result.Mensaje });
        }
    }

    const ClickBtnBuscar = async () => {
        let URL = `http://localhost:${props.ApiPort}/api/ApiTipoProducto/ConsultaTipoPNombre?Categoria=${Gestor.Nombre_Categoria}`;
        let Categorias = await fetch(URL).then(response => response.json()).catch(error => console.log(error));
        if (Categorias.Codigo >= 0) {
            setListaCategorias(Categorias.Contenido);
        } else {
            Swal.fire({ icon: "error", title: "Oops...", text: Categorias.Mensaje });
        }       
    }

    const ClickBtnDescargar = async () => {
        let URL = `http://localhost:${props.ApiPort}/api/ApiSucursal/ReporteSucursales?Nombre_Sucursal=${Gestor.Nombre_Sucursal}&Descripcion=${Gestor.Descripcion}`;
        let Result = await fetch(URL).then(response => response.json()).catch(error => console.log(error));
        if (Result.Codigo >= 0) {
            const templink = document.createElement('a');
            templink.href = `data:application/pdf;base64,${Result.Contenido}`;
            templink.setAttribute('download', 'Reporte Categorías.pdf');
            templink.click();
        } else {
            Swal.fire({ icon: "error", title: "Oops...", text: Result.Mensaje });
        }       
    }

    const cargarCategorias = async () => {
        let URL = `http://localhost:${props.ApiPort}/api/ApiTipoProducto/ConsultaTipoPNombre?Categoria=${Gestor.Nombre_Categoria}`;
        let Categorias = await fetch(URL).then(response => response.json()).catch(error => console.log(error));
        if (Categorias.Codigo >= 0) {
            setListaCategorias(Categorias.Contenido);
        } else {
            Swal.fire({ icon: "error", title: "Oops...", text: Categorias.Mensaje });
        }
    }

    useEffect(() => {
        cargarCategorias();
    }, []);

    return (
        <div>
            <EncabezadoCategorias
                ClickBtnBuscar={ClickBtnBuscar}
                ClickBtnDescargar={ClickBtnDescargar}
                ClickBtnNuevo={ClickBtnNuevo}
                teclaPresionada={teclaPresionada}
                Gestor={Gestor}
            />
            <DetalleBusquedaCategorias
                ListaCategorias={ListaCategorias}
                DetallesCategorias={DetallesCategorias}
                ClickBtnDelete={ClickBtnDelete}
            />
        </div>
    );
}

export default BusquedaCategorias;
