import React, { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2'

const DetalleProveedor = (props) => {
    const navigate = useNavigate();
    const [Proveedor, setCategoria] = useState({
        ID_Proveedor: 0,
        Nombre_Proveedor: '',
        Telefono:0,
        Direccion:''
    })

    const CambioValor = (e) => {
        const { name, type, value, checked } = e.target;
        setCategoria({
            ...Proveedor,
            [name]: type === 'checkbox' ? checked : value
        });
    }

    const tituloTexto = props.IdProveedorActual > 0 ? "Editar Proveedor producto" : "Nueva Proveedor producto";

    const ClickBtnGuardar = async() => {
        let URL= props.IdProveedorActual===0 ? `http://localhost:${props.ApiPort}/api/ApiProveedor/InsertarProveedor` : `http://localhost:${props.ApiPort}/api/ApiProveedor/ModificarProveedor`
        let Method = props.IdProveedorActual===0 ? "POST" : "PATCH"
        let Result= await fetch(URL,{
            "method":Method,
            "headers":{
                "content-type":"application/json",
                "accept":"application/json",
                "Access-Control-Allow-Origin":"*"
            },
            "body": JSON.stringify(Categoria)
        }).then(async response =>{
            if(!response.ok){
                console.log("No Ok")
                throw new Error (response.statusText)
            }
            return await response.json()
        }).catch(error=>{
            console.log(error)
        })
        
        if(Result.Codigo>=0){
            Swal.fire({
                icon: "success",
                title: "Información guardada exitosamente",
                showConfirmButton: false,
                timer: 1500
              });
            navigate("/Proveedor/BusquedaProveedor")
        }
        else{
            Swal.fire({
                icon: "error",
                title: "Oops...",
                text: Result.Mensaje
              });
            //console.log(Result.Mensaje)
        }
    }

    const cargandoFormulario = async () => {
        if (props.IdProveedorActual > 0) {
            let URL = `http://localhost:${props.ApiPort}/api/ApiTipoProducto/ConsultaTipoProductoxID?ID_TipoProducto=${props.IdCategoriaActual}`
            let Proveedores = await fetch(URL).then(async response => {
                if (!response.ok) {
                    console.log("No Ok")
                    throw new Error(response.statusText)
                }
                return await response.json()
            }).catch(error => {
                console.log(error)
            })
            if (Proveedores.Codigo >= 0) {
                setCategoria(Proveedores.Contenido[0])
            }
            else {
                Swal.fire({
                    icon: "error",
                    title: "Oops...",
                    text: Proveedores.Mensaje
                  });
                //console.log(Sucursales.Mensaje)
            }
        }
    }

    useEffect(() => {
        cargandoFormulario();
        //eslint-disable-next-line
    }, [])




}
export default DetalleProveedor;
