import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles.css'; // Importar el archivo CSS

function HomePage() {
  const navigate = useNavigate();

  useEffect(() => {
    const buttons = document.querySelectorAll('button');
    buttons.forEach((button) => {
      button.addEventListener('mouseover', () => {
        button.classList.add('btn-hover');
      });
      button.addEventListener('mouseout', () => {
        button.classList.remove('btn-hover');
      });
    });
    return () => {
      buttons.forEach((button) => {
        button.removeEventListener('mouseover', () => {});
        button.removeEventListener('mouseout', () => {});
      });
    };
  }, []);

  return (
    <div className="home-page">
      <div className="content-box">
        <h1>Bienvenido</h1>
        <p>Selecciona una opci√≥n:</p>
        <button className="btn" onClick={() => navigate("/Sucursales/BusquedaSucursales")}>Sucursales</button>
        <button className="btn" onClick={() => navigate("/")}>Proveedor</button>
        <button className="btn" onClick={() => navigate("/")}>Producto</button>
        <button className="btn" onClick={() => navigate("/TipoProducto/BusquedaCategorias")}>Categorias de producto</button>
      </div>
    </div>
  );
}

export default HomePage;
