import React from "react";
import { useNavigate } from "react-router-dom";
import 'bootstrap-icons/font/bootstrap-icons.css';
import DetalleBusquedaCategorias from "./DetalleBusquedaCategorias"; // Asegúrate de importar el componente de tabla
import backgroundImage from '../img/FondoInicio.jpg';

const EncabezadoCategorias = (props) => {
  const navigate = useNavigate();
  return (
    <div style={{ backgroundImage: 'url(/path/to/your/background.jpg)', backgroundSize: 'cover', padding: '20px', minHeight: '70vh' }}>
      <div style={{ backgroundColor: '#ffffff', borderRadius: '10px', padding: '20px', margin: '0 auto', maxWidth: '90%', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
        <h3 style={{ textAlign: 'center', color: '#333' }}>Categorías</h3>
        <div className="container mt-3">
          <div className="row mb-3">
            <div className="col-12 col-md-3 text-md-right d-flex align-items-center justify-content-md-end">
              <button className="btn btn-secondary" onClick={() => navigate("/")}><i className="bi bi-house-door"></i> Home</button>
            </div>
          </div>
          <div className="row mb-3">
            <div className="col-12 col-md-3 text-md-right d-flex align-items-center justify-content-md-end">
              <label className="form-label mb-0">Nombre de Categoria</label>
            </div>
            <div className="col-12 col-md-4">
              <input type="text" className="form-control" name="Nombre_Categoria" id="Nombre_Categoria" value={props.Gestor.Nombre_Categoria} onChange={props.teclaPresionada} />
            </div>
            <div className="col-12 col-md-2 d-flex justify-content-md-start mt-2 mt-md-0">
              <button className="btn btn-success w-100" onClick={() => props.ClickBtnBuscar()}><i className="bi bi-search"></i> Buscar Categoria</button>
            </div>
            <div className="col-12 col-md-2 d-flex justify-content-md-start mt-2 mt-md-0">
              <button className="btn btn-success w-100" onClick={() => props.ClickBtnDescargar()}><i className="bi bi-cloud-download"></i> Descargar</button>
            </div>
          </div>
          <div className="row">
            <div className="col-12 col-md-2 d-flex justify-content-md-start mt-2 mt-md-0">
              <button className="btn btn-success w-100" onClick={() => props.ClickBtnNuevo()}><i className="bi bi-plus-lg"></i> Nueva Categoria</button>
            </div>
          </div>
          {/* Aquí se incluye el componente de la tabla */}
          <div className="mt-4">
            <DetalleBusquedaCategorias 
              ListaCategorias={props.ListaCategorias}
              DetallesCategorias={props.DetallesCategorias}
              ClickBtnDelete={props.ClickBtnDelete}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default EncabezadoCategorias;
