import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from '../img/FondoInicio.jpg';

function HomePage() {
  const navigate = useNavigate();

  useEffect(() => {
    const buttons = document.querySelectorAll('button');
    buttons.forEach((button) => {
      button.addEventListener('mouseover', () => {
        button.style.backgroundColor = styles.buttonHover.backgroundColor;
      });
      button.addEventListener('mouseout', () => {
        button.style.backgroundColor = styles.button.backgroundColor;
      });
    });
    return () => {
      buttons.forEach((button) => {
        button.removeEventListener('mouseover', () => { });
        button.removeEventListener('mouseout', () => { });
      });
    };
  }, []);

  return (
    <div style={{ ...styles.homePage, backgroundImage: `url(${backgroundImage})` }}>
      <div style={styles.contentBox}>
        <h1 style={styles.heading}>Bienvenido</h1>
        <p style={styles.paragraph}>Selecciona una opci√≥n:</p>
        <button style={styles.button} onClick={() => navigate("/Sucursales/BusquedaSucursales")}>Sucursales</button>
        <button style={styles.button} onClick={() => navigate("/")}>Proveedor</button>
        <button style={styles.button} onClick={() => navigate("/")}>Producto</button>
      </div>
    </div>
  );
}

const styles = {
  homePage: {
    backgroundColor: '#d4d4d4',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  contentBox: {
    backgroundColor: '#f0f0f0',
    padding: '40px',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
    maxWidth: '400px',
    width: '100%',
  },
  heading: {
    marginBottom: '20px',
  },
  paragraph: {
    marginBottom: '30px',
  },
  button: {
    backgroundColor: '#8fbf70',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    margin: '10px 0',
    width: '100%',
    fontSize: '16px',
  },
  buttonHover: {
    backgroundColor: '#7da660',
  },
};

export default HomePage;
