import React, {useState,useEfffect} from "react";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2'
import DetalleBusquedaProveedor from "./DetalleBusquedaProveedor";
import EncabezadoProveedor from "./EncabezadoProveedor";

const BusquedaProveedor=(props)=>{
    const [Gestor,setGestor]= useState({
        Nombre_Proveedor:''
    })

    const navigate = useNavigate();

    const teclaPresionada = (e) => {
        const { name, value } = e.target
        setGestor({ ...Gestor, [name]: value })
    }

    const ClickBtnNuevo = () => {
        props.CambiarIdProveedor(0)
        navigate("/Proveedor/DetalleProveedor")
    }

    const DetalleProveedor = (Descripcion) => {
        //console.log(ID_Sucursal);
        props.CambiarIdProveedor(Descripcion)
        navigate("/Proveedor/DetalleProveedor")
    }

    const ClickBtnDelete = async (ID_TipoProducto) => {
        let URL = `http://localhost:${props.ApiPort}/api/ApiTipoProducto/EliminarTipoProducto?ID_TipoProducto=${ID_TipoProducto}`;
        let Result = await fetch(URL, {
            method: 'DELETE'
        }).then(async response => {
            if (!response.ok) {
                console.log("No Ok - ClickBtnDelete")
                throw new Error(response.statusText)
            }
            return await response.json()
        }).catch(error => {
            console.log(error)
        })
        if (Result.Codigo >= 0) {
            Swal.fire({
                icon: "success",
                title: "Sucursal eliminada exitosamente",
                showConfirmButton: false,
                timer: 1500
              });
              ClickBtnBuscar()
        }
        else {
            Swal.fire({
                icon: "error",
                title: "Oops...",
                text: Result.Mensaje
              });
            //console.log(Sucursales.Mensaje)
        }
    }

    const ClickBtnBuscar = async () => {
        let URL = `http://localhost:${props.ApiPort}/api/ApiTipoProducto/ConsultaTipoPNombre?Categoria=${Gestor.Nombre_Categoria}`;
        let Categorias = await fetch(URL).then(async response => {
            if (!response.ok) {
                console.log("No Ok - ClickBtnBuscar")
                throw new Error(response.statusText)
            }
            return await response.json()
        }).catch(error => {
            console.log(error)
        })
        if (Categorias.Codigo >= 0) {
            setListaCategorias(Categorias.Contenido)
        }
        else {
            Swal.fire({
                icon: "error",
                title: "Oops...",
                text: Categorias.Mensaje
              });
            //console.log(Sucursales.Mensaje)
        }       
    }

    const cargarCategorias = async () => {
        let URL = `http://localhost:${props.ApiPort}/api/ApiTipoProducto/ConsultaTipoPNombre?Categoria=${Gestor.Nombre_Categoria}`
        let Categorias = await fetch(URL).then(async response => {
            if (!response.ok) {
                console.log("No Ok")
                throw new Error(response.statusText)
            }
            return await response.json()
        }).catch(error => {
            console.log(error)
        })
        if (Categorias.Codigo >= 0) {
            setListaCategorias(Categorias.Contenido)
        }
        else {
            Swal.fire({
                icon: "error",
                title: "Oops...",
                text: Categorias.Mensaje
              });
            //console.log(Categorias.Mensaje)
        }
    }

    const [ListaCategorias,setListaCategorias]=useState([])

    useEffect(() => {
        cargarCategorias();
        //eslint-disable-next-line
    }, [])

    return <>
        <EncabezadoProveedor ClickBtnNuevo={ClickBtnNuevo} 
        ClickBtnBuscar={ClickBtnBuscar}
        ClickBtnDescargar={ClickBtnDescargar}
        teclaPresionada={teclaPresionada}
        Gestor={Gestor} />
        <DetalleBusquedaCategorias ListaCategorias={ListaCategorias} 
        DetalleCategorias={DetalleCategorias}
        ClickBtnDelete={ClickBtnDelete}/>
    </>


}
export default BusquedaProveedor;
