import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';

const DetalleProducto = (props) => {
    const navigate = useNavigate();
    const [Producto, setProducto] = useState({
        ID_Producto: 0,
        Nombre_Producto: '',
        Descripcion: '',
        ID_TipoProducto: '',
        Fecha_Caducidad: '',
        ID_Proveedor: '',
        CostoUnitario: ''
    });

    const CambioValor = (e) => {
        const { name, value } = e.target;
        setProducto({ ...Producto, [name]: value });
    };

    const GuardarProducto = async () => {
        let URL = props.IdProductoActual === 0 ? 'http://localhost:2000/api/ApiProductos/InsertarProducto' : 'http://localhost:2000/api/ApiProductos/ModificarProducto';
        let Resul = await fetch(URL, {
            "method": "POST",
            "headers": {
                "content-type": "application/json",
                "accept": "application/json",
                "Access-Control-Allow-Origin": "*"
            },
            "body": JSON.stringify(Producto)
        }).then(async response => {
            if (!response.ok) {
                console.log("No Ok");
                throw new Error(response.statusText);
            }
            return await response.json();
        }).catch(error => {
            console.log(error);
        });

        if (Resul.Codigo >= 0) {
            Swal.fire({
                position: "top-end",
                icon: "success",
                title: "Producto guardado con Ã©xito",
                showConfirmButton: false,
                timer: 1500
            });
            navigate("/Productos/BusquedaProductos");
        } else {
            Swal.fire({
                icon: "error",
                title: "Oops...",
                text: Resul.Mensaje,
                footer: '<a href="#">Why do I have this issue?</a>'
            });
        }
    };

    const cargandoFormulario = async () => {
        if (props.IdProductoActual > 0) {
            let URL = 'http://localhost:2000/api/ApiProductos/ConsultarProductoxID?ID_Producto=' + props.IdProductoActual;
            let Productos = await fetch(URL).then(async response => {
                if (!response.ok) {
                    console.log("No Ok");
                    throw new Error(response.statusText);
                }
                return await response.json();
            }).catch(error => {
                console.log(error);
            });

            if (Productos.Codigo >= 0) {
                setProducto(Productos.Contenido[0]);
            } else {
                console.log(Productos.Mensaje);
            }
        }
    };

    const [ListaTP, setListaTP] = useState([]);
    const Categorias = async () => {
        let URL = 'http://localhost:2000/api/ApiCategorias/ConsultarCategoria?BuscarCategoria=' + Producto.ID_TipoProducto;
        let Categorias = await fetch(URL).then(async response => {
            if (!response.ok) {
                console.log("No Ok");
                throw new Error(response.statusText);
            }
            return await response.json();
        }).catch(error => {
            console.log(error);
        });

        if (Categorias.Codigo >= 0) {
            setListaTP(Categorias.Contenido);
        } else {
            console.log(Categorias.Mensaje);
        }
    };

    useEffect(() => {
        cargandoFormulario();
        Categorias();
        //eslint-disable-next-line
    }, []);

    return <>
        <div style={{ backgroundColor: '#d4d4d4', padding: '20px' }}>
            <header style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '55px', backgroundColor: '#6f9954' }}>
                <h3 style={{ margin: 0, textAlign: 'center', color: "white" }}>Productos</h3>
            </header>
            <div className="container mt-3">
                <div className="row mb-3">
                    <div className="col-12 col-md-3 text-md-right d-flex align-items-center justify-content-md-end">
                        <label className="form-label mb-0">Nombre de Producto</label>
                    </div>
                    <div className="col-12 col-md-4">
                        <input type="text" className="form-control" name="Nombre_Producto" id="Nombre_Producto" value={Producto.Nombre_Producto} onChange={CambioValor} />
                    </div>
                </div>
                <div className="row mb-3">
                    <div className="col-12 col-md-3 text-md-right d-flex align-items-center justify-content-md-end">
                        <label className="form-label mb-0">DescripciÃ³n</label>
                    </div>
                    <div className="col-12 col-md-4">
                        <input type="text" className="form-control" name="Descripcion" id="Descripcion" value={Producto.Descripcion} onChange={CambioValor} />
                    </div>
                </div>
                <div className="row mb-3">
                    <div className="col-12 col-md-3 text-md-right d-flex align-items-center justify-content-md-end">
                        <label className="form-label mb-0">CategorÃ­a</label>
                    </div>
                    <div className="col-12 col-md-4">
                        <select className="form-select" name="ID_TipoProducto" id="ID_TipoProducto" onChange={CambioValor} value={Producto.ID_TipoProducto}>
                            {ListaTP.map((item, i) => (
                                <option key={i} value={item.ID_TipoProducto}>{item.Nombre_TipoProducto}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="row mb-3">
                    <div className="col-12 col-md-3 text-md-right d-flex align-items-center justify-content-md-end">
                        <button className="btn btn-dark w-100" onClick={() => GuardarProducto()}>Guardar</button>
                    </div>
                    <div className="col-12 col-md-2 d-flex justify-content-md-start mt-2 mt-md-0">
                        <button className="btn btn-dark w-100" onClick={() => navigate("/Productos/BusquedaProductos")}>Salir</button>
                    </div>
                </div>
            </div>
        </div>
    </>
}

export default DetalleProducto;
